import "./styles.css";
import { Component } from "react";
import { List } from "./List";
import { Form } from "./Form";

export default class App extends Component {
  constructor(props) {
    super(props);
    this.state = {
      todos: [
        { text: "Do the laundry" },
        { text: "Iron the clothes" },
        { text: "Go for a walk" }
      ]
    };
  }
  handleAdd = (value) => {
    // complete the function to add a new Todo to the list
    this.setState((prevState) => {
      return {
        todos: [...prevState, {text: value}]
      }
    }, () => console.log(this.state.todos, "todo"));
  };

  handleRemove = (row) => {
    // complete the function to remove the Todo from the list
    console.log("handleRemove");
  };
  render() {
    return (
      <div className="App">
        <span>Todo</span>
        {/* Pass the todos list and function as props to utilize those in the component for adding and removing */}
        <Form
          handleAdd={this.handleAdd.bind(this)}
        />
        {
          this.state.todos?.map((elem, index) => {
            return (
              <List
                todo={elem}
                handleRemove={this.handleRemove.bind(this)}
                key={index}
              />
            )
          })
        }

      </div>
    );
  }
}
